import React, { createContext, useState, useContext, useEffect } from 'react';

const PlayerContext = createContext();

const initialGameProgress = {
  puzzles: [],
  completedScrambleTasks: [],
  taskScores: {},
  completedTasks: [],
  scrambleScores: {},
  drawings: [],
  lastPlayed: null
};

export const PlayerProvider = ({ children }) => {
  const [playerName, setPlayerName] = useState(localStorage.getItem('playerName') || '');
  const [showNameModal, setShowNameModal] = useState(!playerName);
  const [gameProgress, setGameProgress] = useState(() => {
    const saved = localStorage.getItem('gameProgress');
    return saved ? JSON.parse(saved) : initialGameProgress;
  });

  const updatePlayerName = (name) => {
    setPlayerName(name);
    localStorage.setItem('playerName', name);
    setShowNameModal(false);
  };

  const updateGameProgress = (gameType, progress) => {
    setGameProgress(prev => {
      const newProgress = { ...prev };

      switch (gameType) {
        case 'puzzle':
          if (!newProgress.puzzles) {
            newProgress.puzzles = [];
          }
          newProgress.puzzles.push({
            id: progress.imageId,
            date: new Date().toISOString(),
            difficulty: progress.difficulty,
            errors: progress.errors,
            totalPieces: progress.difficulty * progress.difficulty,
            accuracy: calculateAccuracy(progress.difficulty * progress.difficulty, progress.errors)
          });
          break;

        case 'drawing':
          if (!newProgress.drawings) {
            newProgress.drawings = [];
          }
          if (progress) {
            newProgress.drawings.push({
              date: new Date().toISOString(),
              ...progress
            });
          }
          break;

        case 'scramble':
          if (!newProgress.completedScrambleTasks) {
            newProgress.completedScrambleTasks = [];
          }
          if (!newProgress.scrambleScores) {
            newProgress.scrambleScores = {};
          }
          if (progress) {
            newProgress.completedScrambleTasks.push(progress.taskId);
            newProgress.scrambleScores[progress.taskId] = progress.score;
          }
          break;

        case 'task':
          if (!newProgress.completedTasks) {
            newProgress.completedTasks = [];
          }
          if (!newProgress.taskScores) {
            newProgress.taskScores = {};
          }
          if (progress) {
            newProgress.completedTasks.push(progress.taskId);
            newProgress.taskScores[progress.taskId] = progress.score;
          }
          break;
      }

      newProgress.lastPlayed = new Date().toISOString();
      localStorage.setItem('gameProgress', JSON.stringify(newProgress));

      return newProgress;
    });
  };

  const calculateAccuracy = (totalPieces, errors) => {
    const correctFirstTry = totalPieces - errors;
    return Math.round((correctFirstTry / totalPieces) * 100);
  };

  const value = {
    playerName,
    showNameModal,
    gameProgress,
    updatePlayerName,
    updateGameProgress
  };

  return (
    <PlayerContext.Provider value={value}>
      {children}
    </PlayerContext.Provider>
  );
};

export const usePlayer = () => {
  const context = useContext(PlayerContext);
  if (!context) {
    throw new Error('usePlayer must be used within a PlayerProvider');
  }
  return context;
};
