import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const MenuContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
`;

const MenuTitle = styled.h1`
  color: #2196F3;
  text-align: center;
  margin-bottom: 40px;
  font-size: 2.5rem;
`;

const MenuGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  width: 100%;
`;

const MenuItem = styled.button`
  background-color: white;
  border: 2px solid #2196F3;
  border-radius: 15px;
  padding: 30px;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 15px rgba(33, 150, 243, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const MenuIcon = styled.span`
  font-size: 3rem;
`;

const MenuText = styled.span`
  font-size: 1.2rem;
  color: #333;
  font-weight: bold;
`;

const MenuDescription = styled.p`
  color: #666;
  font-size: 0.9rem;
  text-align: center;
  margin: 0;
`;

const MainMenu = () => {
  const navigate = useNavigate();

  const menuItems = [
    {
      id: 'puzzle',
      icon: 'üß©',
      title: '·Éû·Éê·Éñ·Éö·Éî·Éë·Éò',
      description: '·Éê·Éê·É¨·Éß·Éï·Éî ·É°·Éê·ÉÆ·Éê·Éö·Éò·É°·Éù ·Éû·Éê·Éñ·Éö·Éî·Éë·Éò',
    },
    {
      id: 'tasks',
      icon: 'üéØ',
      title: '·É°·Éê·ÉÆ·Éê·Éö·Éò·É°·Éù ·Éê·Éõ·Éù·É™·Éê·Éú·Éî·Éë·Éò',
      description: '·Éí·Éê·Éõ·Éù·Éò·É™·Éê·Éú·Éò ·Éì·Éê ·Éê·Éõ·Éù·ÉÆ·É°·Éî·Éú·Éò',
    },
    {
      id: 'stories',
      icon: 'üìö',
      title: '·Éñ·É¶·Éê·Éû·É†·Éî·Éë·Éò',
      description: '·É¨·Éê·Éò·Éô·Éò·Éó·ÉÆ·Éî ·Éì·Éê ·É£·Éû·Éê·É°·É£·ÉÆ·Éî ·Éô·Éò·Éó·ÉÆ·Éï·Éî·Éë·É°',
    },
    {
      id: 'photos',
      icon: 'üñºÔ∏è',
      title: '·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·Éì·Éê·Éö·Éê·Éí·Éî·Éë·Éê',
      description: '·Éì·Éê·Éê·Éö·Éê·Éí·Éî ·É§·Éù·É¢·Éù·Éî·Éë·Éò ·É°·É¨·Éù·É†·Éê·Éì',
    },
  ];

  const handleGameSelect = (path) => {
    navigate(`/${path}`);
  };

  return (
    <MenuContainer>
      <MenuTitle>üåü ·É°·Éê·ÉÆ·Éê·Éö·Éò·É°·Éù ·Éó·Éê·Éõ·Éê·É®·Éî·Éë·Éò üåü</MenuTitle>
      <MenuGrid>
        {menuItems.map((item) => (
          <MenuItem key={item.id} onClick={() => handleGameSelect(item.id)}>
            <MenuIcon>{item.icon}</MenuIcon>
            <MenuText>{item.title}</MenuText>
            <MenuDescription>{item.description}</MenuDescription>
          </MenuItem>
        ))}
      </MenuGrid>
    </MenuContainer>
  );
};

export default MainMenu;
