import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';

const PlayerContext = createContext();

const initialGameProgress = {
  puzzles: [],
  completedScrambleTasks: [],
  taskScores: {},
  completedTasks: [],
  scrambleScores: {},
  coloringProgress: {},
  lastPlayed: null
};

export const PlayerProvider = ({ children }) => {
  const [playerName, setPlayerName] = useState(localStorage.getItem('playerName') || '');
  const [showNameModal, setShowNameModal] = useState(!playerName);
  const [gameProgress, setGameProgress] = useState(() => {
    const saved = localStorage.getItem('gameProgress');
    return saved ? JSON.parse(saved) : initialGameProgress;
  });

  const updatePlayerName = (name) => {
    setPlayerName(name);
    localStorage.setItem('playerName', name);
    setShowNameModal(false);
  };

  const updateGameProgress = useCallback((gameType, taskId, progress) => {
    setGameProgress(prev => {
      const newProgress = { ...prev };

      switch(gameType) {
        case 'coloring':
          if (!newProgress.coloringProgress) {
            newProgress.coloringProgress = {};
          }
          newProgress.coloringProgress[taskId] = {
            ...newProgress.coloringProgress[taskId],
            ...progress,
            lastPlayed: Date.now()
          };
          break;

        case 'tasks':
          if (!newProgress.completedTasks) {
            newProgress.completedTasks = [];
          }
          if (!newProgress.completedTasks.includes(taskId)) {
            newProgress.completedTasks = [...newProgress.completedTasks, taskId];
          }
          newProgress.taskScores = {
            ...newProgress.taskScores,
            [taskId]: progress
          };
          break;

        case 'scramble':
          if (!newProgress.completedScrambleTasks) {
            newProgress.completedScrambleTasks = [];
          }
          if (!newProgress.completedScrambleTasks.includes(taskId)) {
            newProgress.completedScrambleTasks = [...newProgress.completedScrambleTasks, taskId];
          }
          break;
      }

      localStorage.setItem('gameProgress', JSON.stringify(newProgress));
      return newProgress;
    });
  }, []);

  // დამხმარე ფუნქცია სიზუსტის გამოსათვლელად
  const calculateAccuracy = (totalPieces, errors) => {
    const correctFirstTry = totalPieces - errors;
    return Math.round((correctFirstTry / totalPieces) * 100);
  };

  const value = {
    playerName,
    showNameModal,
    gameProgress,
    updatePlayerName,
    updateGameProgress
  };

  return (
    <PlayerContext.Provider value={value}>
      {children}
    </PlayerContext.Provider>
  );
};

export const usePlayer = () => {
  const context = useContext(PlayerContext);
  if (!context) {
    throw new Error('usePlayer must be used within a PlayerProvider');
  }
  return context;
};
