import React, { createContext, useState, useContext, useEffect } from 'react';

const PlayerContext = createContext();

export const PlayerProvider = ({ children }) => {
  const [playerName, setPlayerName] = useState('');
  const [showNameModal, setShowNameModal] = useState(true);
  const [gameProgress, setGameProgress] = useState({
    completedTasks: [],
    taskScores: {},
    completedScrambleTasks: [],
    scrambleScores: {},
    lastPlayedDate: null
  });

  useEffect(() => {
    const savedName = localStorage.getItem('playerName');
    const savedProgress = localStorage.getItem('gameProgress');

    if (savedName) {
      setPlayerName(savedName);
      setShowNameModal(false);
    }

    if (savedProgress) {
      setGameProgress(JSON.parse(savedProgress));
    }
  }, []);

  const updatePlayerName = (name) => {
    setPlayerName(name);
    localStorage.setItem('playerName', name);
    setShowNameModal(false);
  };

  const updateGameProgress = (type, completedItems, scores) => {
    setGameProgress(prev => {
      let newProgress;

      if (type === 'tasks') {
        newProgress = {
          ...prev,
          completedTasks: [...new Set([...prev.completedTasks, completedItems])],
          taskScores: {
            ...prev.taskScores,
            [completedItems]: Math.max(scores, prev.taskScores[completedItems] || 0)
          }
        };
      } else if (type === 'scramble') {
        newProgress = {
          ...prev,
          completedScrambleTasks: completedItems,
          scrambleScores: scores
        };
      }

      newProgress = {
        ...newProgress,
        lastPlayedDate: new Date().toISOString()
      };

      localStorage.setItem('gameProgress', JSON.stringify(newProgress));
      return newProgress;
    });
  };

  const resetProgress = () => {
    const newProgress = {
      completedTasks: [],
      taskScores: {},
      completedScrambleTasks: [],
      scrambleScores: {},
      lastPlayedDate: null
    };
    setGameProgress(newProgress);
    localStorage.setItem('gameProgress', JSON.stringify(newProgress));
  };

  return (
    <PlayerContext.Provider value={{ 
      playerName, 
      showNameModal, 
      setShowNameModal,
      updatePlayerName,
      gameProgress,
      updateGameProgress,
      resetProgress
    }}>
      {children}
    </PlayerContext.Provider>
  );
};

export const usePlayer = () => {
  const context = useContext(PlayerContext);
  if (!context) {
    throw new Error('usePlayer must be used within a PlayerProvider');
  }
  return context;
};
