import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const GameContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  width: 100%;
  max-height: calc(100vh - 200px);
`;

const TopSection = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 1rem;
  margin-bottom: 1rem;
`;

const RightSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const BottomSection = styled.div`
  display: flex;
  justify-content: center;
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(10px);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  border-radius: 1rem;
  padding: 1rem;
  height: fit-content;
`;

const CardTitle = styled.h3`
  font-size: 1rem;
  font-weight: 500;
  margin-bottom: 0.5rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: #2196F3;
`;

const ImageContainer = styled.div`
  position: relative;
  width: calc((100% - ${props => (props.size - 1) * 2}px) / ${props => props.size});
  aspect-ratio: 1;
  border-radius: 0.5rem;
  overflow: hidden;
  border: 2px solid rgba(33, 150, 243, 0.2);
  margin: 0 auto;
`;

const OriginalImage = styled.div`
  width: 100%;
  height: 100%;
  background-image: url(${props => props.image});
  background-size: ${props => props.size * 100}% ${props => props.size * 100}%;
  background-position: center;
`;

const Board = styled.div`
  display: grid;
  grid-template-columns: repeat(${props => props.size}, 1fr);
  gap: 2px;
  background-color: ${props => props.isTarget ? 'rgba(245, 245, 245, 0.5)' : 'transparent'};
  padding: 0.5rem;
  border-radius: 0.5rem;
