            onChange={(e) => setName(e.target.value)}
            autoFocus
          />
          <Button type="submit">·Éì·Éê·Éï·Éò·É¨·Éß·Éù·Éó ·Éó·Éê·Éõ·Éê·É®·Éò! üéÆ</Button>
        </form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default NameModal;
