import React, { useState } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const DrawingGame = () => {
  const { updateGameProgress } = usePlayer();
  const [selectedColor, setSelectedColor] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);

  const colors = [
    { value: '#FFA500' }, // ნარინჯისფერი
    { value: '#90EE90' }, // მწვანე
    { value: '#FF0000' }  // წითელი
  ];

  const handleColorSelect = (color) => {
    setSelectedColor(color.value);
    const isCorrect = color.value === '#FF0000';
    
    if (isCorrect) {
      setShowFeedback(true);
      updateGameProgress('drawing', Date.now(), { completed: true });
      setTimeout(() => {
        setShowFeedback(false);
      }, 1500);
    }
  };

  return (
    <Container>
      <Title>ხატვა</Title>
      
      <GameArea>
        {/* სანიმუშო გული */}
        <ExampleHeart>
          <svg width="150" height="150" viewBox="0 0 100 100">
            <path
              d="M50 80 C 25 60, 0 40, 0 20 C 0 0, 20 0, 25 10 C 30 0, 50 0, 50 20 C 50 0, 70 0, 75 10 C 80 0, 100 0, 100 20 C 100 40, 75 60, 50 80"
              fill="#FF0000"
              stroke="none"
            />
          </svg>
        </ExampleHeart>

        {/* გასაფერადებელი გული */}
        <DrawingArea>
          <svg width="300" height="300" viewBox="0 0 100 100">
            <path
              d="M50 80 C 25 60, 0 40, 0 20 C 0 0, 20 0, 25 10 C 30 0, 50 0, 50 20 C 50 0, 70 0, 75 10 C 80 0, 100 0, 100 20 C 100 40, 75 60, 50 80"
              fill={selectedColor || 'transparent'}
              stroke="#FF0000"
              strokeWidth="2"
            />
          </svg>
        </DrawingArea>

        {/* ფერების პალიტრა */}
        <ColorPalette>
          {colors.map((color, index) => (
            <ColorButton
              key={index}
              color={color.value}
              onClick={() => handleColorSelect(color)}
            />
          ))}
        </ColorPalette>

        {showFeedback && (
          <Feedback>ყოჩაღ! სწორად გააფერადე! 🎉</Feedback>
        )}
      </GameArea>
    </Container>
  );
};

const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  padding: 2rem;
  background-color: #fff;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 2rem;
`;

const GameArea = styled.div`
  max-width: 800px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
`;

const ExampleHeart = styled.div`
  position: absolute;
  top: 2rem;
  right: 2rem;
`;

const DrawingArea = styled.div`
  width: 100%;
  max-width: 500px;
  aspect-ratio: 1;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: white;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const ColorButton = styled.button`
  width: 60px;
  height: 60px;
  border: none;
  border-radius: 8px;
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

const Feedback = styled.div`
  font-size: 1.5rem;
  color: #4CAF50;
  text-align: center;
`;

export default DrawingGame;
