const Dashboard = () => {
  const { playerName, gameProgress } = usePlayer();
  const [activeTab, setActiveTab] = useState('puzzle');
  const [chartData, setChartData] = useState([]);
  const [trendImproving, setTrendImproving] = useState(false);

  useEffect(() => {
    console.log('gameProgress:', gameProgress);
    if (gameProgress && gameProgress.puzzles) {
      // Filter only 3x3 puzzles and get last 10 games
      const threePuzzles = gameProgress.puzzles
        .filter(puzzle => puzzle.difficulty === 3)
        .slice(-10);

      console.log('3x3 Puzzles:', threePuzzles);

      const newChartData = threePuzzles.map((puzzle, index) => ({
        name: `თამაში ${index + 1}`,
        შეცდომები: puzzle.errors
      }));

      console.log('Chart Data:', newChartData);
      setChartData(newChartData);

      // Calculate trend
      if (newChartData.length >= 2) {
        const firstHalf = newChartData.slice(0, Math.floor(newChartData.length / 2));
        const secondHalf = newChartData.slice(Math.floor(newChartData.length / 2));

        const firstHalfAvg = firstHalf.reduce((acc, curr) => acc + curr.შეცდომები, 0) / firstHalf.length;
        const secondHalfAvg = secondHalf.reduce((acc, curr) => acc + curr.შეცდომები, 0) / secondHalf.length;

        setTrendImproving(secondHalfAvg < firstHalfAvg);
      }
    }
  }, [gameProgress]);

  const calculateProgress = (completed, total) => {
    return Math.round((completed / total) * 100);
  };

  const getTotalTasks = () => {
    switch (activeTab) {
      case 'tasks':
        return 100;
      case 'scramble':
        return 30;
      case 'puzzle':
        return Math.max(10, gameProgress.completedPuzzles.length);
      default:
        return 0;
    }
  };

  const renderStats = () => {
    const total = getTotalTasks();
    let completed;
    let progressText;

    switch (activeTab) {
      case 'tasks':
        completed = gameProgress.completedTasks.length;
        progressText = `${completed} / ${total} სახალისო დავალება`;
        break;
      case 'scramble':
        completed = gameProgress.completedScrambleTasks.length;
        progressText = `${completed} / ${total} ასოების დალაგება`;
        break;
      case 'puzzle':
        completed = gameProgress.completedPuzzles.length;
        progressText = `${completed} დასრულებული პაზლი`;
        break;
      default:
        completed = 0;
        progressText = '';
    }

    const progress = calculateProgress(completed, activeTab === 'puzzle' ? completed : total);

    return (
      <StatsCard
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
      >
        <StatsHeader>{progressText}</StatsHeader>
        <StatsGrid>
          <div>
            <StatValue>
              {completed} <span>{activeTab !== 'puzzle' && ` / ${total}`}</span>
            </StatValue>
            <StatLabel>{activeTab === 'puzzle' ? 'დასრულებული პაზლები' : 'შესრულებული დავალებები'}</StatLabel>
          </div>
          {activeTab !== 'puzzle' && (
            <div>
              <StatValue>
                {Object.values(gameProgress.taskScores).length > 0
                  ? (Object.values(gameProgress.taskScores).reduce((a, b) => a + b, 0) / Object.values(gameProgress.taskScores).length).toFixed(1)
                  : '0.0'}
              </StatValue>
              <StatLabel>საშუალო ქულა</StatLabel>
            </div>
          )}
        </StatsGrid>
        <ProgressBar>
          <Progress
            type={activeTab}
            percent={progress}
          />
        </ProgressBar>
        <ProgressText>
          {progress}% დასრულებული
        </ProgressText>
      </StatsCard>
    );
  };

  const renderTabs = () => {
    return (
      <>
        <TabButton
          active={activeTab === 'tasks'}
          onClick={() => setActiveTab('tasks')}
        >
          <Book size={window.innerWidth <= 480 ? 16 : 20} />
          <span>სახალისო</span>
        </TabButton>
        <TabButton
          active={activeTab === 'scramble'}
          onClick={() => setActiveTab('scramble')}
        >
          <AlignLeft size={window.innerWidth <= 480 ? 16 : 20} />
          <span>ასოები</span>
        </TabButton>
        <TabButton
          active={activeTab === 'puzzle'}
          onClick={() => setActiveTab('puzzle')}
        >
          <PuzzlePiece size={window.innerWidth <= 480 ? 16 : 20} />
          <span>პაზლები</span>
        </TabButton>
      </>
    );
  };

  return (
    <DashboardContainer>
      <ContentWrapper>
        <Title>მოგესალმები, {playerName}! 👋</Title>

        <TabContainer>
          {renderTabs()}
        </TabContainer>

        {renderStats()}

        {console.log('Rendering chart, chartData:', chartData)}
        {chartData && chartData.length > 0 ? (
          <ChartContainer>
            <StatsHeader>3x3 პაზლების შეცდომების სტატისტიკა</StatsHeader>

            <TrendIndicator improving={trendImproving}>
              {trendImproving ? (
                <>
                  <TrendingDown size={18} />
                  მოსწავლე უკეთესდება - შეცდომების რაოდენობა მცირდება
                </>
              ) : (
                <>
                  <TrendingUp size={18} />
                  მოსწავლეს უჭირს - შეცდომების რაოდენობა იზრდება
                </>
              )}
            </TrendIndicator>

            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={chartData} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255, 255, 255, 0.2)" />
                <XAxis dataKey="name" stroke="#fff" />
                <YAxis 
                  stroke="#fff"
                  label={{
                    value: 'შეცდომების რაოდენობა',
                    angle: -90,
                    position: 'insideLeft',
                    fill: '#fff'
                  }}
                />
                <Tooltip
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.1)',
                    border: 'none',
                    backdropFilter: 'blur(10px)',
                    color: 'white'
                  }}
                />
                <Line
                  type="monotone"
                  dataKey="შეცდომები"
                  stroke="#FFD700"
                  strokeWidth={2}
                  dot={{ fill: '#FFD700' }}
                  activeDot={{ r: 8 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </ChartContainer>
        ) : (
          <ChartContainer>
            <StatsHeader>ჯერ არ არის 3x3 პაზლების მონაცემები</StatsHeader>
          </ChartContainer>
        )}
      </ContentWrapper>
    </DashboardContainer>
  );
};

export default Dashboard;
