import React, { useState, useEffect } from 'react';
import { usePlayer } from '../context/PlayerContext';
import { motion } from 'framer-motion';
import { Book, AlignLeft, PuzzleIcon as PuzzlePiece } from 'lucide-react';
import styled from 'styled-components';

const DashboardContainer = styled.div`
  min-height: 100vh;
  width: 100%;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  background: linear-gradient(to bottom right, #1a237e, #4a148c, #880e4f);
  padding: 2rem;
  color: white;
  overflow-y: auto;
  z-index: 1000;
  box-sizing: border-box;

  * {
    box-sizing: border-box;
  }

  &::-webkit-scrollbar {
    display: none;
  }

  -ms-overflow-style: none;
  scrollbar-width: none;

  @media (max-width: 768px) {
    padding: 1rem;
  }
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: bold;
  margin-bottom: 2rem;
  text-align: center;

  @media (max-width: 768px) {
    font-size: 2rem;
    margin-bottom: 1.5rem;
  }

  @media (max-width: 480px) {
    font-size: 1.75rem;
    margin-bottom: 1rem;
  }
`;

const TabContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 0.5rem;
  margin-bottom: 1rem;

  @media (max-width: 768px) {
    justify-content: center;
    flex-wrap: wrap;
  }
`;

const TabButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  border-radius: 9999px;
  transition: all 0.2s;
  background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.2)'};
  color: ${props => props.active ? '#4a148c' : 'white'};
  white-space: nowrap;

  &:hover {
    background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.3)'};
  }

  @media (max-width: 480px) {
    padding: 0.4rem 0.8rem;
    font-size: 0.9rem;

    svg {
      width: 16px;
      height: 16px;
    }
  }
`;

const StatsCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 0.5rem;
  padding: 1.5rem;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    padding: 1.25rem;
  }

  @media (max-width: 480px) {
    padding: 1rem;
  }
`;

const StatsHeader = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 1rem;
`;

const StatsGrid = styled.div`
  display: grid;
  gap: 1rem;
  margin: 1rem 0;
`;

const StatValue = styled.div`
  font-size: 2.5rem;
  font-weight: bold;
  color: #2ed573;
  margin-bottom: 0.5rem;
`;

const StatLabel = styled.div`
  font-size: 1rem;
  color: #a4b0be;
  margin-bottom: 0.5rem;
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 0.5rem;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 9999px;
  margin: 0.5rem 0;
  overflow: hidden;
`;

const ProgressFill = styled.div`
  height: 100%;
  border-radius: 9999px;
  background: ${props => props.color};
  width: ${props => props.value}%;
`;

const Dashboard = () => {
  const { playerName, gameProgress } = usePlayer();
  const [activeTab, setActiveTab] = useState('puzzle');

  const calculatePuzzleStats = () => {
    if (!gameProgress?.completedPuzzles) return { completed: 0, correctMoves: 0, totalPieces: 0 };

    const puzzles = gameProgress.completedPuzzles;
    const totalCompleted = puzzles.length;

    let totalPieces = 0;
    let totalCorrectMoves = 0;

    puzzles.forEach(puzzle => {
      const pieces = puzzle.difficulty * puzzle.difficulty;
      totalPieces += pieces;
      totalCorrectMoves += pieces - puzzle.errors;
    });

    return {
      completed: totalCompleted,
      correctMoves: totalCorrectMoves,
      totalPieces: totalPieces
    };
  };

  const renderStats = () => {
    const stats = calculatePuzzleStats();
    const accuracy = stats.totalPieces > 0 
      ? Math.round((stats.correctMoves / stats.totalPieces) * 100) 
      : 0;

    return (
      <StatsGrid>
        <StatsCard>
          <StatValue>{stats.completed}</StatValue>
          <StatLabel>დასრულებული პაზლი</StatLabel>
          <ProgressBar>
            <ProgressFill value={accuracy} color="#2ed573" />
          </ProgressBar>
          <StatLabel>
            სწორი სვლები: {stats.correctMoves} / {stats.totalPieces} ({accuracy}%)
          </StatLabel>
        </StatsCard>
      </StatsGrid>
    );
  };

  return (
    <DashboardContainer>
      <ContentWrapper>
        <Title>მოგესალმები, {playerName}! 👋</Title>

        <TabContainer>
          <TabButton
            active={activeTab === 'tasks'}
            onClick={() => setActiveTab('tasks')}
          >
            <Book size={window.innerWidth <= 480 ? 16 : 20} />
            <span>სახალისო</span>
          </TabButton>
          <TabButton
            active={activeTab === 'scramble'}
            onClick={() => setActiveTab('scramble')}
          >
            <AlignLeft size={window.innerWidth <= 480 ? 16 : 20} />
            <span>ასოები</span>
          </TabButton>
          <TabButton
            active={activeTab === 'puzzle'}
            onClick={() => setActiveTab('puzzle')}
          >
            <PuzzlePiece size={window.innerWidth <= 480 ? 16 : 20} />
            <span>პაზლები</span>
          </TabButton>
        </TabContainer>

        {renderStats()}
      </ContentWrapper>
    </DashboardContainer>
  );
};

export default Dashboard;
