import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import styled from 'styled-components';
import MainMenu from './components/MainMenu';
import Tasks from './components/Tasks';
import PuzzleGame from './components/PuzzleGame';
import ScrambleGame from './components/ScrambleGame';
import HamburgerMenu from './components/HamburgerMenu';
import { PlayerProvider } from './context/PlayerContext';
import { useNavigate } from 'react-router-dom';

const Container = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #f5f5f5;
  padding: 20px;
`;

const HomeButton = styled.button`
  position: fixed;
  top: 20px;
  left: 20px;
  font-size: 2rem;
  background: none;
  border: none;
  cursor: pointer;
  transition: transform 0.3s ease;
  z-index: 1000;

  &:hover {
    transform: scale(1.1);
  }
`;

const HomeButtonWrapper = ({ children }) => {
  const navigate = useNavigate();
  return (
    <>
      {window.location.pathname !== '/' && (
        <HomeButton onClick={() => navigate('/')}>üè†</HomeButton>
      )}
      {children}
    </>
  );
};

function App() {
  return (
    <PlayerProvider>
      <Router>
        <Container>
          <HamburgerMenu />
          <Routes>
            <Route path="/" element={<HomeButtonWrapper><MainMenu /></HomeButtonWrapper>} />
            <Route path="/tasks" element={<HomeButtonWrapper><Tasks /></HomeButtonWrapper>} />
            <Route path="/puzzle" element={<HomeButtonWrapper><PuzzleGame /></HomeButtonWrapper>} />
            <Route path="/scramble" element={<HomeButtonWrapper><ScrambleGame /></HomeButtonWrapper>} />
          </Routes>
        </Container>
      </Router>
    </PlayerProvider>
  );
}

export default App;
