import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { usePlayer } from '../context/PlayerContext';
import { images as allImages } from '../data/newphotos';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const SetupContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
  margin-bottom: 20px;
`;

const NumberSelector = styled.div`
  display: flex;
  gap: 10px;
  margin: 20px 0;
`;

const NumberButton = styled.button`
  padding: 10px 20px;
  font-size: 18px;
  border: 2px solid #4CAF50;
  border-radius: 5px;
  background-color: ${props => props.selected ? '#4CAF50' : 'white'};
  color: ${props => props.selected ? 'white' : '#4CAF50'};
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background-color: #4CAF50;
    color: white;
  }
`;

const StartButton = styled.button`
  padding: 15px 30px;
  font-size: 20px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background-color: #45a049;
  }
`;

const ImagesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 20px;
  width: 100%;
  max-width: 800px;
  margin-top: 20px;
`;

const ImageCard = styled.div`
  border: 2px solid #ddd;
  border-radius: 10px;
  padding: 10px;
  cursor: ${props => props.draggable ? 'grab' : 'default'};
  background-color: white;
  transition: all 0.3s ease;

  &:hover {
    transform: ${props => props.draggable ? 'scale(1.05)' : 'none'};
    box-shadow: ${props => props.draggable ? '0 5px 15px rgba(0,0,0,0.1)' : 'none'};
  }
`;

const Image = styled.img`
  width: 100%;
  height: 150px;
  object-fit: cover;
  border-radius: 5px;
`;

const ImageTitle = styled.p`
  text-align: center;
  margin: 10px 0 0 0;
  font-size: 16px;
`;

const Timer = styled.div`
  font-size: 24px;
  margin: 20px 0;
  color: #333;
`;

const Message = styled.div`
  font-size: 24px;
  color: #333;
  margin: 20px 0;
  text-align: center;
`;

const MemoryGame = () => {
  const [selectedNumber, setSelectedNumber] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);
  const [images, setImages] = useState([]);
  const [shuffledImages, setShuffledImages] = useState([]);
  const [timer, setTimer] = useState(null);
  const [showMessage, setShowMessage] = useState('');
  const [canDrag, setCanDrag] = useState(false);
  const { updateGameProgress } = usePlayer();
  const navigate = useNavigate();

  const shuffleArray = (array) => {
    const newArray = [...array];
    for (let i = newArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
    }
    return newArray;
  };

  const selectRandomImages = (count) => {
    const shuffled = shuffleArray(allImages);
    return shuffled.slice(0, count);
  };

  const handleNumberSelect = (number) => {
    setSelectedNumber(number);
  };

  const startGame = () => {
    if (!selectedNumber) return;

    const selectedImages = selectRandomImages(selectedNumber);
    setImages(selectedImages);
    setShuffledImages(selectedImages);
    setGameStarted(true);

    // 5 ·É¨·Éê·Éõ·Éò·Éê·Éú·Éò ·Éì·Éê·Éó·Éï·Éê·Éö·Éò·Éî·É†·Éî·Éë·Éò·É° ·Éì·É†·Éù
    setTimer(5);
    const timerInterval = setInterval(() => {
      setTimer(prev => {
        if (prev <= 1) {
          clearInterval(timerInterval);
          setShowMessage('·Éì·Éê·ÉÆ·É£·É≠·Éî ·Éó·Éï·Éê·Éö·Éî·Éë·Éò!');

          // 2 ·É¨·Éê·Éõ·Éò·É° ·É®·Éî·Éõ·Éì·Éî·Éí ·Éï·É£·É†·Éî·Éï·Éó ·É°·É£·É†·Éê·Éó·Éî·Éë·É°
          setTimeout(() => {
            setShowMessage('');
            setShuffledImages(shuffleArray(selectedImages));
            setCanDrag(true);
          }, 2000);

          return null;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const handleDragStart = (e, index) => {
    if (!canDrag) return;
    e.dataTransfer.setData('text/plain', index);
  };

  const handleDragOver = (e) => {
    if (!canDrag) return;
    e.preventDefault();
  };

  const handleDrop = (e, targetIndex) => {
    if (!canDrag) return;
    e.preventDefault();
    const sourceIndex = parseInt(e.dataTransfer.getData('text/plain'));

    const newImages = [...shuffledImages];
    [newImages[sourceIndex], newImages[targetIndex]] = [newImages[targetIndex], newImages[sourceIndex]];
    setShuffledImages(newImages);

    // ·É®·Éî·Éï·Éê·Éõ·Éù·É¨·Éõ·Éù·Éó ·É°·É¨·Éù·É†·Éê·Éì ·Éê·É†·Éò·É° ·Éó·É£ ·Éê·É†·Éê ·Éì·Éê·Éö·Éê·Éí·Éî·Éë·É£·Éö·Éò
    const isCorrect = newImages.every((img, idx) => img === images[idx]);
    if (isCorrect) {
      setShowMessage('·Éí·Éò·Éö·Éù·É™·Éê·Éï! ·É°·É¨·Éù·É†·Éê·Éì ·Éì·Éê·Éê·Éö·Éê·Éí·Éî! üéâ');
      setCanDrag(false);
      updateGameProgress('memory', Date.now(), { score: 100 });

      setTimeout(() => {
        setGameStarted(false);
        setSelectedNumber(null);
        setShowMessage('');
      }, 2000);
    }
  };

  return (
    <Container>
      {!gameStarted ? (
        <SetupContainer>
          <h2>·Éê·Éò·É†·É©·Éò·Éî ·É°·É£·É†·Éê·Éó·Éî·Éë·Éò·É° ·É†·Éê·Éù·Éì·Éî·Éú·Éù·Éë·Éê</h2>
          <NumberSelector>
            {[3, 4, 5].map(num => (
              <NumberButton
                key={num}
                selected={selectedNumber === num}
                onClick={() => handleNumberSelect(num)}
              >
                {num}
              </NumberButton>
            ))}
          </NumberSelector>
          <StartButton onClick={startGame} disabled={!selectedNumber}>
            ·Éì·Éê·É¨·Éß·Éî·Éë·Éê
          </StartButton>
        </SetupContainer>
      ) : (
        <>
          {timer && <Timer>{timer} ·É¨·Éê·Éõ·Éò</Timer>}
          {showMessage && <Message>{showMessage}</Message>}
          <ImagesGrid>
            {shuffledImages.map((image, index) => (
              <ImageCard
                key={index}
                draggable={canDrag}
                onDragStart={(e) => handleDragStart(e, index)}
                onDragOver={handleDragOver}
                onDrop={(e) => handleDrop(e, index)}
              >
                <Image src={image.url} alt={image.title} />
                <ImageTitle>{image.title}</ImageTitle>
              </ImageCard>
            ))}
          </ImagesGrid>
        </>
      )}
    </Container>
  );
};

export default MemoryGame;
