import React, { createContext, useState, useContext, useEffect } from 'react';

const PlayerContext = createContext();

export const PlayerProvider = ({ children }) => {
  const [playerName, setPlayerName] = useState(localStorage.getItem('playerName') || '');
  const [showNameModal, setShowNameModal] = useState(!playerName);
  const [gameProgress, setGameProgress] = useState(() => {
    const savedProgress = localStorage.getItem('gameProgress');
    return savedProgress ? JSON.parse(savedProgress) : {
      puzzles: [],
      completedTasks: [],
      taskScores: {},
      completedScrambleTasks: [],
      scrambleScores: {},
      lastPlayed: null
    };
  });

  const updatePlayerName = (name) => {
    setPlayerName(name);
    localStorage.setItem('playerName', name);
    setShowNameModal(false);
  };

  const updateGameProgress = (gameType, imageId, progress) => {
    setGameProgress(prev => {
      const newProgress = { ...prev };

      // ინიციალიზაცია თუ არ არსებობს
      if (!newProgress.puzzleScores) {
        newProgress.puzzleScores = {};
      }
      if (!newProgress.totalPuzzles) {
        newProgress.totalPuzzles = 0;
      }
      if (!newProgress.completedPuzzles) {
        newProgress.completedPuzzles = 0;
      }

      // დავამატოთ ახალი სქორი
      newProgress.puzzleScores[imageId] = {
        ...progress,
        timestamp: new Date().toISOString()
      };

      // განვაახლოთ საერთო სტატისტიკა
      newProgress.totalPuzzles = Object.keys(newProgress.puzzleScores).length;
      newProgress.completedPuzzles += 1;

      // შევინახოთ localStorage-ში
      localStorage.setItem('gameProgress', JSON.stringify(newProgress));

      return newProgress;
    });
  };

  const value = {
    playerName,
    showNameModal,
    gameProgress,
    updatePlayerName,
    updateGameProgress
  };

  return (
    <PlayerContext.Provider value={value}>
      {children}
    </PlayerContext.Provider>
  );
};

export const usePlayer = () => {
  const context = useContext(PlayerContext);
  if (!context) {
    throw new Error('usePlayer must be used within a PlayerProvider');
  }
  return context;
};
