const StatisticsTitle = styled.h2`
  color: white;
  margin-top: 20px;
  text-align: center;
`;

const Dashboard = () => {
  const { playerName, gameProgress } = usePlayer();
  const [activeTab, setActiveTab] = useState('puzzles');
  const [selectedDifficulty, setSelectedDifficulty] = useState('all');

  const puzzles = gameProgress.puzzles || [];

  const chartData = useMemo(() => {
    const filteredPuzzles = selectedDifficulty === 'all' 
      ? puzzles 
      : puzzles.filter(p => p.difficulty === parseInt(selectedDifficulty));

    // ვითვლით საშუალო შეცდომებს თითოეული სირთულისთვის
    const difficultyErrors = {
      '3': {
        puzzles: puzzles.filter(p => p.difficulty === 3),
        avgErrors: 0
      },
      '4': {
        puzzles: puzzles.filter(p => p.difficulty === 4),
        avgErrors: 0
      },
      '5': {
        puzzles: puzzles.filter(p => p.difficulty === 5),
        avgErrors: 0
      }
    };

    // გამოვთვალოთ საშუალო შეცდომები
    Object.keys(difficultyErrors).forEach(difficulty => {
      const puzzleSet = difficultyErrors[difficulty].puzzles;
      if (puzzleSet.length > 0) {
        difficultyErrors[difficulty].avgErrors = 
          puzzleSet.reduce((sum, p) => sum + p.errors, 0) / puzzleSet.length;
      }
    });

    return {
      labels: ['3x3', '4x4', '5x5'],
      datasets: [{
        label: 'საშუალო შეცდომები',
        data: [
          difficultyErrors['3'].avgErrors,
          difficultyErrors['4'].avgErrors,
          difficultyErrors['5'].avgErrors
        ],
        borderColor: '#FFD700',
        backgroundColor: 'rgba(255, 215, 0, 0.1)',
        tension: 0.4,
        fill: true
      }]
    };
  }, [puzzles, selectedDifficulty]);

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: true,
        grid: {
          color: 'rgba(255, 255, 255, 0.1)'
        },
        ticks: {
          color: '#fff'
        }
      },
      x: {
        grid: {
          color: 'rgba(255, 255, 255, 0.1)'
        },
        ticks: {
          color: '#fff'
        }
      }
    },
    plugins: {
      legend: {
        labels: {
          color: '#fff'
        }
      }
    }
  };

  const calculateProgress = (completed, total) => {
    return Math.round((completed / total) * 100);
  };

  const getTotalTasks = () => {
    switch (activeTab) {
      case 'tasks':
        return 100;
      case 'scramble':
        return 30;
      case 'puzzle':
        return Math.max(10, gameProgress.completedPuzzles.length);
      default:
        return 0;
    }
  };

  const renderStats = () => {
    const total = getTotalTasks();
    let completed;
    let progressText;

    switch (activeTab) {
      case 'tasks':
        completed = gameProgress.completedTasks.length;
        progressText = `${completed} / ${total} სახალისო დავალება`;
        break;
      case 'scramble':
        completed = gameProgress.completedScrambleTasks.length;
        progressText = `${completed} / ${total} ასოების დალაგება`;
        break;
      case 'puzzle':
        completed = gameProgress.completedPuzzles.length;
        progressText = `${completed} დასრულებული პაზლი`;
        break;
      default:
        completed = 0;
        progressText = '';
    }

    const progress = calculateProgress(completed, activeTab === 'puzzle' ? completed : total);

    return (
      <StatsCard
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
      >
        <StatsHeader>{progressText}</StatsHeader>
        <StatsGrid>
          <div>
            <StatValue>
              {completed} <span>{activeTab !== 'puzzle' && ` / ${total}`}</span>
            </StatValue>
            <StatLabel>{activeTab === 'puzzle' ? 'დასრულებული პაზლები' : 'შესრულებული დავალებები'}</StatLabel>
          </div>
          {activeTab !== 'puzzle' && (
            <div>
              <StatValue>
                {Object.values(gameProgress.taskScores).length > 0
                  ? (Object.values(gameProgress.taskScores).reduce((a, b) => a + b, 0) / Object.values(gameProgress.taskScores).length).toFixed(1)
                  : '0.0'}
              </StatValue>
              <StatLabel>საშუალო ქულა</StatLabel>
            </div>
          )}
        </StatsGrid>
        <ProgressBar>
          <Progress
            type={activeTab}
            percent={progress}
          />
        </ProgressBar>
        <ProgressText>
          {progress}% დასრულებული
        </ProgressText>
      </StatsCard>
    );
  };

  return (
    <DashboardContainer>
      <ContentWrapper>
        <Title>მოგესალმები, {playerName}! 👋</Title>

        <TabContainer>
          <TabButton
            active={activeTab === 'tasks'}
            onClick={() => setActiveTab('tasks')}
          >
            <Book size={window.innerWidth <= 480 ? 16 : 20} />
            <span>სახალისო</span>
          </TabButton>
          <TabButton
            active={activeTab === 'scramble'}
            onClick={() => setActiveTab('scramble')}
          >
            <AlignLeft size={window.innerWidth <= 480 ? 16 : 20} />
            <span>ასოები</span>
          </TabButton>
          <TabButton
            active={activeTab === 'puzzles'}
            onClick={() => setActiveTab('puzzles')}
          >
            <PuzzlePiece size={window.innerWidth <= 480 ? 16 : 20} />
            <span>პაზლები</span>
          </TabButton>
        </TabContainer>

        {renderStats()}

        {activeTab === 'puzzles' && (
          <>
            <StatisticsTitle>პაზლების სტატისტიკა</StatisticsTitle>
            <ChartContainer>
              <Line data={chartData} options={chartOptions} />
            </ChartContainer>
          </>
        )}

        <ChartContainer>
          <StatsHeader>
            {activeTab === 'tasks' && 'ბოლო 7 დღის აქტივობა'}
            {activeTab === 'scramble' && 'პროგრესი სირთულის მიხედვით'}
            {activeTab === 'puzzles' && 'პაზლების სტატისტიკა'}
          </StatsHeader>
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={chartData} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255, 255, 255, 0.2)" />
              <XAxis dataKey="name" stroke="#fff" />
              <YAxis stroke="#fff" label={{ value: 'რაოდენობა', angle: -90, position: 'insideLeft', fill: '#fff' }} />
              <Tooltip
                contentStyle={{
                  backgroundColor: 'rgba(255, 255, 255, 0.1)',
                  border: 'none',
                  backdropFilter: 'blur(10px)',
                  color: 'white'
                }}
              />
              {/* Add your chart lines here */}
            </LineChart>
          </ResponsiveContainer>
        </ChartContainer>
      </ContentWrapper>
    </DashboardContainer>
  );
};

export default Dashboard;
